import javax.persistence.EntityManagerFactory;
import javax.persistence.Persistence;
import java.time.LocalDateTime;

public class RemarkTest {
    private static EntityManagerFactory emf;
    private static EntityManager em;
    private static UserService userService;
    private static RemarkService remarkService;

    public static void main(String[] args) {
        // Initialize the EntityManagerFactory and EntityManager
        emf = Persistence.createEntityManagerFactory("your-persistence-unit-name");
        em = emf.createEntityManager();

        // Initialize the UserService and RemarkService
        userService = new UserService(em);
        remarkService = new RemarkService(em);

        // Create a new User
