        user.setEmail("test@example.com");
        user.setPassword("password");
        // Set other user properties as needed

        // Persist the User
        em.getTransaction().begin();
        em.persist(user);
        em.getTransaction().commit();

        // Create a new Remark
        Remark remark = new Remark();
        remark.setDescription("This is a test remark");
        remark.setPriority("HIGH");
        remark.setStatus("OPEN");
        remark.setType("FEEDBACK");
        remark.setUser(user);
        remark.setCreatedAt(LocalDateTime.now());
        remark.setUpdatedAt(LocalDateTime.now());

        // Persist the Remark
        em.getTransaction().begin();
        remarkService.registerRemark(user, remark);
        em.getTransaction().commit();

        // Close the EntityManager and EntityManagerFactory
        em.close();
        emf.close();
    }
}
```

