@Entity
@Table(name = &quot;remarks&quot;)
public class Remark {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(name = &quot;remark_id&quot;, nullable = false)
    @JsonProperty(&quot;remark_id&quot;)
    private Long id;

    @Column(name = &quot;remark_text&quot;, nullable = false)
    private String description;

